void tri_Kubatko2D(int N, VectorXd &rfq, VectorXd &sfq, VectorXd &wfq){
#define loadNfq(M)  Nfq = Q_GLegendre_N##M
#define loadfq(M)				\
	rfq(i) = Q_GLegendre_rq_N##M[i]; sfq(i) = Q_GLegendre_sq_N##M[i]; wfq(i) = Q_GLegendre_wq_N##M[i];

// #define loadNfq(M)  Nfq = Q_GLobatto_N##M
// #define loadfq(M)				\
// 	rfq(i) = Q_GLobatto_rq_N##M[i]; sfq(i) = Q_GLobatto_sq_N##M[i]; wfq(i) = Q_GLobatto_wq_N##M[i];

	int Nfq;
  	if( N < 0 or N > 6 ){
  		printf( "N is not valid to Kubatko2D\n" );
  		printf( "N has to be an integer between 1 and 6\n" );
  		printf( "Using N = 1 as default\n" );
  		Nfq = 1;
  	}else{
		switch(N){
			case 1: loadNfq(1); break;
			case 2: loadNfq(2); break;
			case 3: loadNfq(3); break;
			case 4: loadNfq(4); break;
			case 5: loadNfq(5); break;
			case 6: loadNfq(6); break;
		}
	}
	rfq.resize(Nfq);  sfq.resize(Nfq);  wfq.resize(Nfq);
	for (int i = 0; i < Nfq; ++i){
		switch(N){
			case 1: loadfq(1); break;
			case 2: loadfq(2); break;
			case 3: loadfq(3); break;
			case 4: loadfq(4); break;
			case 5: loadfq(5); break;
			case 6: loadfq(6); break;
		}
	}
}